Select round((ST_DISTANCE(ST_SETSRID(ST_POINT(?,?),4326)::geography,geography)/1609.34)::numeric,2) as distance, "Trail_Name", "System_Name"  from trails_with_geog where "Trail_Name" in ('Coastal Trail','Ship Creek Trail','Glenn Hwy Trail', 'Glenn Hwy. Trail', 'Campbell Creek Trail', 'Chester Creek Trail', 'Northern Lights Bvld. Trail', 'Goose Lake Trail', 'ELMORE ROAD') order by distance asc limit 1